import React, { Fragment, Component } from "react";
import { paginate } from "../../util/paginate";
import FilterBar from "./filterbar/FilterBar";
import MapContainer from "./MapContainer/MapContainer";
import RestaurauntsList from "./RestaurantContainer/RestaurauntsList";

class ResultsContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: 1,
      pageSize: 5,
      ratings: [5, 4, 3, 2, 1]
    };

    this.handleRatings = this.handleRatings.bind(this);
    this.handleOpenNowFilter = this.handleOpenNowFilter.bind(this);
    this.handlePageChange = this.handlePageChange.bind(this);
  }

  handleRatings() {
    console.log(this.state.ratings);
  }
  storeRestaurants = gPlacesRestaurants => {
    this.setState({
      googleRestaurants: gPlacesRestaurants
    });
  };
  handleOpenNowFilter() {}

  handlePageChange(page) {
    this.setState({
      currentPage: page
    });
  }

  render() {
    console.log("Render meth Results Container", this.state);
    const {
      googleRestaurants: allRestaruants,
      ratings,
      pageSize,
      currentPage
    } = this.state;

    const restaraunts = paginate(allRestaruants, currentPage, pageSize);

    const { lat, lng } = this.props;
    return (
      <Fragment>
        <section className="app__filterBar">
          <FilterBar
            onStarSelect={this.handleRatings}
            // onSelectedNum={selectedRating}
            ratings={ratings}
          />
        </section>{" "}
        <main className="app__results-viewport">
          <section className="app__map">
            <div className="app__map-container">
              <MapContainer
                lat={lat}
                lng={lng}
                restaraunts={restaraunts}
                storeRestaurants={this.storeRestaurants}
              />
            </div>{" "}
          </section>{" "}
          <section className="app__results-column">
            <RestaurauntsList
              restarauntResults={restaraunts}
              restarauntTotal={allRestaruants.length}
              pageSize={pageSize}
              currentPage={currentPage}
              onPageChange={this.handlePageChange}
            />
          </section>
        </main>
      </Fragment>
    );
  }
}

export default ResultsContainer;
