import React from "react";
import RatingsFilterItems from "./RatingsFilterItems";
// import PropTypes from 'prop-types';
import ArrowDropDownIcon from "@material-ui/icons/ArrowDropDown";
import ArrowDropUpIcon from "@material-ui/icons/ArrowDropUp";

class RatingsFilter extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      arrowDown: true,
      showItems: false
    };
  }

  render() {
    // console.log(this.props);
    return (
      <section>
        <button className=" btn btn__style" type="button">
          {this.props.label}
          {this.state.showItems ? (
            <span>
              <ArrowDropDownIcon> ArrowDropDownIcon </ArrowDropDownIcon>
            </span>
          ) : (
            <ArrowDropUpIcon> ArrowDropUpIcon </ArrowDropUpIcon>
          )}
        </button>
        <div className={{ display: this.state.showItems ? "block" : "none" }}>
          <RatingsFilterItems />
        </div>
      </section>
    );
  }
}

export default RatingsFilter;
