import React, { Component } from "react";
import { GoogleApiWrapper } from "google-maps-react";
import { getRestaraunts } from "../appContainer/util/getRMyRestaurants";
import { paginate } from "../appContainer/util/paginate";
import Header from "./Components/Header/Header";
import ResultsContainer from "../appContainer/Components/ResultsContainer/ResultsContainer.js";
// import { getGeoLocation } from "./util/geoLocation";

class AppContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      restaraunts: [],
      restaurantsList: [],
      lat: null,
      lng: null,
      currentPage: 1,
      pageSize: 5,
      ratings: [5, 4, 3, 2, 1]
    };

    this.handleRatings = this.handleRatings.bind(this);
    this.handleOpenNowFilter = this.handleOpenNowFilter.bind(this);
    this.handlePageChange = this.handlePageChange.bind(this);
  }

  componentDidMount() {
    this.setState({
      lat: this.props.lat,
      lng: this.props.lng
    });
  }

  handleRatings() {
    console.log(this.state.ratings);
  }

  storeRestaurants = gPlacesRestaurants => {
    this.setState({
      googleRestaurants: gPlacesRestaurants
    });
  };

  handleOpenNowFilter() {}

  handlePageChange(page) {
    this.setState({
      currentPage: page
    });
  }

  render() {
    const { google } = this.props;
    console.log("Render meth Results Container", this.state);
    const {
      googleRestaurants: allRestaruants,
      ratings,
      pageSize,
      currentPage
    } = this.state;

    const restaraunts = paginate(allRestaruants, currentPage, pageSize);

    const { lat, lng } = this.props;

    return (
      <div className="container-fluid app__containter">
        <header className="app__container-header">
          <Header />
        </header>
        <main className="app__container-results">
          <ResultsContainer
            lat={this.state.lat}
            lng={this.state.lng}
            googlgeAPi={google}
          />
        </main>
      </div>
    );
  }
}

export default GoogleApiWrapper(AppContainer);
