import React, { Fragment, Component } from "react";
import Restauraunt from "./Restauraunt";
import Pagination from "./Pagination";
import Sort from "./Sort";

class RestaurauntsList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      pageSize: 4
    };
  }

  render() {
    console.log("Restaraunt State", this.state);
    return (
      <Fragment>
        <section className="rest__container">
          <header className="rest__container-header">
            <div className="rest__results-info">
              Showing {this.props.restarauntTotal} results
            </div>
            <Pagination
              itemsCount={this.props.restarauntTotal}
              pageSize={this.state.pageSize}
            />
            <Sort label={"Sort"} />
          </header>{" "}
          <Restauraunt
            restarauntResults={this.props.restarauntResults}
            reviewText="Customer Review"
          />
        </section>{" "}
      </Fragment>
    );
  }
}

export default RestaurauntsList;
