import React, { Component } from "react";
import { Map, GoogleApiWrapper, Marker, InfoWindow } from "google-maps-react";

class MapContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      selectedPlace: {},
      activeMarker: {},
      showingInfoWindow: false
    };

    this.fetchPlaces = this.fetchPlaces.bind(this);
    this.setPlace = this.setPlace.bind(this);
  }
  UNSAFE_componentWillMount() {}

  fetchPlaces = (mapProps, map) => {
    const { google } = mapProps;
    console.log(google);
    console.log(map);

    const userLocation = new google.maps.LatLng(this.props.lat, this.props.lng);
    const request = {
      location: userLocation,
      radius: "500",
      type: ["restaurant"]
    };

    const service = new google.maps.places.PlacesService(map);
    service.nearbySearch(request, getPlaces);

    function getPlaces(results, status) {
      if (status === google.maps.places.PlacesServiceStatus.OK) {
        for (var i = 0; i < results.length; i++) {
          const place = results[i];
          this.setPlace(place);
        }
      }
    }
  };

  setPlace = places => {
    return places;
  };

  onIconClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  render() {
    console.log(this.setPlace);
    return (
      <Map
        google={this.props.google}
        zoom={14}
        style={{ width: "100%", height: "100%", position: "absolute" }}
        initialCenter={{
          lat: this.props.lat,
          lng: this.props.lng
        }}
        onReady={this.fetchPlaces}
      >
        {this.props.restaraunts &&
          this.props.restaraunts.map(rest => (
            <Marker
              key={rest.place_id}
              position={{
                lat: rest.geometry.location.lat,
                lng: rest.geometry.location.lng
              }}
              name={rest.name}
              onClick={this.onIconClick}
              icon={{
                url: "../../../assests/map_pin_blue.svg"
              }}
            />
          ))}

        <Marker
          onClick={this.onIconClick}
          name={"You are Here"}
          position={{
            lat: this.props.lat,
            lng: this.props.lng
          }}
          clickable={true}
          animation={this.props.google.maps.Animation.DROP}
          icon={{
            url: "../../../assests/placeholder.svg"
            // This marker is 20 pixels wide by 32 pixels high.
          }}
        />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
          onClose={this.onClose}
        >
          <div>
            <p>{this.state.selectedPlace.name}</p>
          </div>
        </InfoWindow>
      </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: "AIzaSyA8Byy23oFligL0X1_WQYca0ABneIhxOow"
})(MapContainer);
