import React, { Component } from "react";
import { Map, GoogleApiWrapper, Marker, InfoWindow } from "google-maps-react";

class MapContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      showInfoWindow: {},
      activeMarker: {},
      showingInfoWindow: false
    };
  }
  UNSAFE_componentWillMount() {}

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };
  render() {
    console.log(this.props);
    return (
      <Map
        google={this.props.google}
        zoom={14}
        style={{ width: "100%", height: "100%", position: "absolute" }}
        initialCenter={{
          lat: this.props.lat,
          lng: this.props.lng
        }}
      >
        {this.props.restaraunts &&
          this.props.restaraunts.map(rest => (
            <Marker
              key={rest.place_id}
              position={{
                lat: rest.geometry.location.lat,
                lng: rest.geometry.location.lng
              }}
              name={rest.name}
              onClick={this.state.onMarkerOver}
            />
          ))}
        <Marker
          onMouse={this.onMarkerClick}
          title={"You are Here"}
          position={{
            lat: this.props.lat,
            lng: this.props.lng
          }}
          clickable={true}
          animation={this.props.google.maps.Animation.DROP}
          image={{
            url:
              "https://developers.google.com/maps/documentation/javascript/examples/full/images/beachflag.png",
            // This marker is 20 pixels wide by 32 pixels high.
            size: this.props.google.maps.Size(20, 32),
            // The origin for this image is (0, 0).
            origin: this.props.google.maps.Point(0, 0),
            // The anchor for this image is the base of the flagpole at (0, 32).
            anchor: this.props.google.maps.Point(0, 32)
          }}
        />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
          onClose={this.onClose}
        >
          <div>
            <h4>{this.state.selectedPlace.name}</h4>
          </div>
        </InfoWindow>
      </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: "AIzaSyA8Byy23oFligL0X1_WQYca0ABneIhxOow"
})(MapContainer);
