import React, { Fragment, Component } from "react";
import RestList from "../../restList.json";
import FilterBar from "./FilterBar";
import MapContainer from "./MapContainer/MapContainer";
import RestaurauntsList from "./RestaurantContainer/RestaurauntsList";

class ResultsContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      starRatingNum: [5, 4, 3, 2, 1],
      data: []
    };

    this.handleStarSelect = this.handleStarSelect.bind(this);
    this.handleOpenNowFilter = this.handleOpenNowFilter.bind(this);
  }
  componentDidMount() {
    this.setState({
      data: RestList
    });
  }
  handleStarSelect(starRatingNum) {
    this.setState({
      selectedRating: starRatingNum
    });
  }

  handleOpenNowFilter() {
    console.log(this.state.data);
  }
  render() {
    const { results } = this.state.data;
    console.log(results);

    // console.log(this.state.data.results);
    const { lat, lng } = this.props;
    return (
      <Fragment>
        <section className="app__filterBar">
          <FilterBar
            onStarSelect={this.handleStarSelect}
            onSelectedNum={this.state.selectedRating}
            starRatingNum={this.state.starRatingNum}
          />
        </section>{" "}
        <main className="app__results-viewport">
          <section className="app__map">
            <div className="app__map-container">
              <MapContainer lat={lat} lng={lng} data={this.state.data} />{" "}
            </div>{" "}
          </section>{" "}
          <section className="app__results-column">
            <RestaurauntsList
              restarauntResults={results}
              resultsLenght={results}
            />
          </section>{" "}
        </main>{" "}
      </Fragment>
    );
  }
}

export default ResultsContainer;
