import React, { Component } from "react";
import GoogleMap from "./index";
import { getRestaraunts } from "../appContainer/util/getRMyRestaurants";
import Header from "./Components/Header/Header";
import ResultsContainer from "../appContainer/Components/ResultsContainer/ResultsContainer";
import { getGeoLocation } from "./util/geoLocation";

class AppContainer extends Component {
  state = {
    restaraunts: [],
    restaurantsList: [],
    lat: null,
    lng: null
  };

  componentDidMount() {
    window.navigator.geolocation.getCurrentPosition(
      position =>
        this.setState({
          lat: position.coords.latitude,
          lng: position.coords.longitude
        }),
      err => this.setState({ errorMessage: err.message })
    );
  }

  storeRestaurants = data => {
    this.setState({
      restaurantsList: data,
      restaraunts: getRestaraunts()
    });
  };
  render() {
    console.log(getGeoLocation());

    return (
      <div className="container-fluid app__containter">
        <header className="app__container-header">
          <Header />
        </header>{" "}
        <main className="app__container-results">
          <ResultsContainer lat={this.state.lat} lng={this.state.lng} />{" "}
        </main>{" "}
      </div>
    );
  }
}

export default AppContainer;
