import React, { Fragment, Component } from "react";
import { getRestRatings } from "../../modules/getRatings";
import { getRestaraunts } from "../../modules/getRestaraunts";
import FilterBar from "./FilterBar";
import MapContainer from "./MapContainer/MapContainer";
import RestaurauntsList from "./RestaurantContainer/RestaurauntsList";

class ResultsContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      starRatingNum: [],
      restaraunts: [],
      pageSize: []
    };

    this.handleStarSelect = this.handleStarSelect.bind(this);
    this.handleOpenNowFilter = this.handleOpenNowFilter.bind(this);
    this.handlePageChange = this.handlePageChange.bind(this);
  }
  componentDidMount() {
    this.setState({
      restaraunts: getRestaraunts(),
      starRatingNum: getRestRatings(),
      pageSize: 10
    });
  }
  handleStarSelect(starRatingNum) {
    // console.log(starRatingNum);
  }

  handleOpenNowFilter() {}

  handlePageChange(page) {
    console.log(page);
  }

  render() {
    const { restaraunts, starRatingNum, pageSize } = this.state;
    // console.log("this is my ratins", starRatingNum);

    // console.log("this is my unfiltered results", restaraunts);
    const filteredItem =
      restaraunts && starRatingNum
        ? restaraunts.filter(filter =>
            console.log("filter log", filter.ratings === "4")
          )
        : restaraunts;

    // console.log("This is my filtered", filteredItem);

    const { lat, lng } = this.props;
    return (
      <Fragment>
        <section className="app__filterBar">
          <FilterBar
            onStarSelect={this.handleStarSelect}
            // onSelectedNum={selectedRating}
            starRatingNum={starRatingNum}
          />
        </section>{" "}
        <main className="app__results-viewport">
          <section className="app__map">
            <div className="app__map-container">
              <MapContainer lat={lat} lng={lng} restaraunts={restaraunts} />
            </div>{" "}
          </section>{" "}
          <section className="app__results-column">
            <RestaurauntsList
              restarauntResults={restaraunts}
              restarauntTotal={restaraunts.length}
              pageSize={pageSize}
              onPageChange={this.handlePageChange}
            />
          </section>{" "}
        </main>{" "}
      </Fragment>
    );
  }
}

export default ResultsContainer;
