import React, { Component } from "react";
import { Map, GoogleApiWrapper, Marker, InfoWindow } from "google-maps-react";

class MapContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      selectedPlace: {},
      activeMarker: {},
      showingInfoWindow: false
    };
  }
  UNSAFE_componentWillMount() {
    const request = {
      location: {
        lat: this.props.lat,
        lng: this.props.lng
      },
      radius: "500",
      type: ["restaurant"]
    };
    const { places } = this.props.google.maps;
    const service = places.PlacesService;
    service.nearbySearch(request);
  }

  onIconClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };
  render() {
    return (
      <Map
        google={this.props.google}
        zoom={14}
        style={{ width: "100%", height: "100%", position: "absolute" }}
        initialCenter={{
          lat: this.props.lat,
          lng: this.props.lng
        }}
      >
        {this.props.restaraunts &&
          this.props.restaraunts.map(rest => (
            <Marker
              key={rest.place_id}
              position={{
                lat: rest.geometry.location.lat,
                lng: rest.geometry.location.lng
              }}
              name={rest.name}
              onClick={this.onIconClick}
              icon={{
                url: "../../../assests/map_pin_blue.svg"
              }}
            />
          ))}

        <Marker
          onClick={this.onIconClick}
          name={"You are Here"}
          position={{
            lat: this.props.lat,
            lng: this.props.lng
          }}
          clickable={true}
          // animation={this.props.google.maps.Animation.DROP}
          icon={{
            url: "../../../assests/placeholder.svg",
            // This marker is 20 pixels wide by 32 pixels high.
            size: Size(20, 32),
            // The origin for this image is (0, 0).
            origin: Point(0, 0),
            // The anchor for this image is the base of the flagpole at (0, 32).
            anchor: Point(0, 32)
          }}
        />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
          onClose={this.onClose}
        >
          <div>
            <p>{this.state.selectedPlace.name}</p>
          </div>
        </InfoWindow>
      </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: "AIzaSyA8Byy23oFligL0X1_WQYca0ABneIhxOow"
})(MapContainer);
