import React, { Component, Fragment } from 'react';

import { Row } from 'react-bootstrap';
import { Col } from 'react-bootstrap';
import FilterBar from './FilterBar';
import MapContainer from './MapContainer/MapContainer';
import RestaurauntsList from './RestaurantContainer/RestaurauntsList';
// import * as restarauntData from '../.././myrestauraunts.json';

class ResultsContainer extends Component {
	state = { lat: null, lon: null };

	componentDidMount() {
		// console.log(restarauntData);
		// console.log('Component Did Mount');
		window.navigator.geolocation.getCurrentPosition(
			position =>
				this.setState({
					lat: position.coords.latitude,
					lon: position.coords.longitude
				}),
			err => this.setState({ errorMessage: err.message })
		);

		fetch(
			`https//vallisweekes.com/portfolio/restaraunt_review/js/restList.json`
		)
			.then(res => console.log(res))
			.catch(err => console.log(err));
	}

	render() {
		return (
			<Fragment>
				<Row>
					<Col>
						<FilterBar />
					</Col>
				</Row>
				<Row>
					if(this.state.lat && this.state.lon)
					{
						<main className='map_restauraunt_wrapper'>
							<Col sm={12} lg={6} xl={7}>
								<section>
									if()
									<MapContainer lat={this.state.lat} lon={this.state.lon} />
								</section>
							</Col>
							<Col sm={12} lg={6} xl={5}>
								<section>
									<RestaurauntsList />
								</section>
							</Col>
						</main>
					}
					if(this.state.errorMessage) {}
				</Row>
			</Fragment>
		);
	}
}

export default ResultsContainer;
